import SearchBar from './SearchBar';
import SearchResults from './SearchResults';
import Playlist from './Playlist';
//import styles from './App.css';
import React, {useState, useEffect} from 'react';
import {transformInput, generateId} from './helperFunctions';
//import Songs from './Songs';

const app ={
   backgroundColor: "#27415C",
   marginTop: -30
};

const title= {
   color: "#A2C2E1",
   textAlign: "center",
   paddingTop: 50
};

const container = {
  backgroundColor: "#A2C2E1",
  width : 900,
  height: 2600,
  marginLeft: 160,
  marginRight : 160

};

const span={
  color: "#ffff"
};

const resultAndPlaylist={
   display: "flex",

};


// App component este parent pt SearchBar component, da props 
function App() {
  const [searchInput, setSearchInput] = useState("");
  // controlam starea la songs obtinute de la server
  //const [resultSongs, setResultSongs]= useState(null);  // nu avem nevoie de asta

  // controlam starea la Songs afisate. toate odata vor fi afisate, viewSongs e un array de obiecte
  const [songsList, setSongsList] =useState([]); 
/*
   viewSong = {
    id : number de la functie
    title : 
    album:
    preview: src
  }
*/


// async function, fetch songs list bye artist from rapidapi.com, returns promise "songs"
  const getSongsbyArtist= async(artist) =>{
    // datas for API request
    const baseUrl = 'https://deezerdevs-deezer.p.rapidapi.com/search';
    const transformedSearchInput = transformInput(artist);
    const requestParams =`?q=${transformedSearchInput}`;
    const url = `${baseUrl}${requestParams}`;

    const options = {
      method: 'GET',
      headers: {
        'X-RapidAPI-Key': '48ae9bfa21mshf797af22061bc12p1069a4jsn184cf3cad960',
        'X-RapidAPI-Host': 'deezerdevs-deezer.p.rapidapi.com'
      }
    };

    try {
      const response = await fetch(url, options);
      if(response.ok){
        const jsonResponse = await response.json();
        const songs = jsonResponse.data;
        return  songs; // ! Important
      }

    }catch (error) {
      console.error(error);
     }   
  } 


  // consuma promisiunea ce o primim  de la  getSongsbyArtist()
  const showSongs = async()=>{
     let songs = await getSongsbyArtist(searchInput);
     console.log("Show Songs: ");
     console.log(songs); // aici avem arayy (25)
     //  facem ceva cu promisiunea
     //let song = songs[0]; // primul obiect song din sirul de 25 songs cu toate atributele
     //console.log(song);

     // array de obiecte cu atributele specifice ce le vrem
     const songsArray = []; 
     console.log("songs array ");
     console.log(songsArray);
     for (let song of songs){
      let songObject ={
        id :generateId() ,
        title: song.title,
        album: song.album.title,
        preview: song.preview,
       };
       songsArray.push(songObject);
       //console.log(songObject); // doar obiectul cu 3 atribute

     }
     setSongsList(songsArray); // state of the songsLista va fi arrayul creat din songs
     console.log("songList: ");
     console.log(songsList);
  }

  function handleChangeInput(e){
     e.preventDefault();
     setSearchInput(e.target.value);
  }



  // onClickSearch prop pt search button
  // doar cand apas pe buton vrem sa ceara lista de songs de la pagina web
  function handleClickSearchButton(e){
    // previne sa revina la starea initiala , deci nu mai dispare rezultatul
    e.preventDefault();
    // consuma promisiunea si schimba starea de la setResultSongs cu raspunsul, adica songs
    // facem aici cererea la server, altfel, de cate ori tastam o litera in input field
    // se schimba starea lui searchInput si se face cererea la server automat, app crashes
    showSongs();// chemam functia async ce a consumat promisiunea intoarsa de la request

  }

  function handleClickAddSong(e){
    e.preventDefault();

  }

  // JSX expresion to return 
  return (
    <div style={app}>
      <h1 style={title}><span style={span}>LISTEN</span>your favorite<span style={span}>MUSIC</span></h1>
      <div style={container}>
        <SearchBar searchInput={searchInput} onChangeInput={handleChangeInput} onClickSearch={handleClickSearchButton} />
        <div style={resultAndPlaylist}>
          <SearchResults songs={songsList} onClickHandle={handleClickAddSong}/>
          <Playlist/>
        </div> 
      </div>
    </div>
  );
}


export default App;
