import logo from './logo.svg';
import SearchBar from './SearchBar';
import SearchResults from './SearchResults';
import Playlist from './Playlist';
import styles from './App.css';
import React, {useState, useEffect} from 'react';
import transformInput from './helperFunctions';


const app ={
   backgroundColor: "#27415C",
   marginTop: -30
};

const title= {
   color: "#A2C2E1",
   textAlign: "center",
   paddingTop: 50
}

const container = {
  backgroundColor: "#A2C2E1",
  width : 900,
  height: 700,
  marginLeft: 160,
  marginRight : 160

}

const span={
  color: "#ffff"
}

const resultAndPlaylist={
   display: "flex",

}


// App este parent pt SearchBar component, da props 
// cand am tastat Search , ia ce e in input si pune in searchResult ,
// deci paragraful unde vrem sa punem ce e in input isi schimba starea,useState
function App() {
  const [searchInput, setSearchInput] = useState("");
  const [resultSongs, setResultSongs]= useState([]);

  // consume the async function 
  // render in results ?

  useEffect(()=>{
    // aici consuma async func getSongsbyArtist
    //console.log(getSongsbyArtist(searchInput));
    // cand nu am tastat un artist, nu face cerere la server
    if (searchInput !==" "){
      getSongsbyArtist(searchInput).then(showSongs);
    }

    // avem nevoie de o functie ce afiseaza song, deci consuma promisiunea si afiseaza apoi

  }, []);

  // consuma getSongsbyArtist si , starea la result Songs se schimbafoloseste display sa afiseze
const showSongs= async()=>{ 
  const songs = await getSongsbyArtist();
  display(songs);
  setResultSongs(resultSongs);

} 

// display song
function display(song){
   console.log("The list of searched songs: "+song);
}
// async function, fetch songs list bye artist from rapidapi.com
const getSongsbyArtist= async(artist) =>{
  // datas for API requests
  const baseUrl = 'https://deezerdevs-deezer.p.rapidapi.com/search';
  const transformedSearchInput = transformInput(artist);
  const requestParams =`?q=${transformedSearchInput}`;
  const url = `${baseUrl}${requestParams}`;
//const url = 'https://deezerdevs-deezer.p.rapidapi.com/search?q=taylor%20swift';
  const options = {
    method: 'GET',
    headers: {
    'X-RapidAPI-Key': '48ae9bfa21mshf797af22061bc12p1069a4jsn184cf3cad960',
    'X-RapidAPI-Host': 'deezerdevs-deezer.p.rapidapi.com'
  }
  };


  try {
    const response = await fetch(url, options);
   if(response.ok){
     const jsonResponse = await response.json();
     console.log(jsonResponse.data); // array data: [{}...]
     const songs = jsonResponse.data;
     return  songs;
   }

}   catch (error) {
    console.error(error);
}


} 

  function handleChangeInput(e){
     e.preventDefault();
     setSearchInput(e.target.value);
  }

  // onClickSearch prop, Butonul
  function handleClickSearchButton(e){
    // previne sa revina la starea initiala , deci nu mai dispare rezultatul
    e.preventDefault();
    //setResult(searchInput);
    // cand apasa pe buton, face request la pagina, consuma promisiunea, apoi afiseaza lista
    // de songs
    getSongsbyArtist(searchInput).then(showSongs);



  }

  return (
    <div style={app}>
      <h1 style={title}><span style={span}>LISTEN</span>your favorite<span style={span}>MUSIC</span></h1>
      <div style={container}>
        <SearchBar searchInput={searchInput} onChangeInput={handleChangeInput} onClickSearch={handleClickSearchButton}/>
        <div style={resultAndPlaylist}>
          <SearchResults result={resultSongs}/>
          <Playlist/>
        </div> 
        <p></p>
      </div>
    </div>
  );
}


export default App;
