  const url = 'https://deezerdevs-deezer.p.rapidapi.com/search?q=eminem';

  const options = {
    method: 'GET',
    headers: {
      'X-RapidAPI-Key': '48ae9bfa21mshf797af22061bc12p1069a4jsn184cf3cad960',
      'X-RapidAPI-Host': 'deezerdevs-deezer.p.rapidapi.com'
    }
  };

  try {
    const response = await fetch(url, options);
   // const result = await response.text();
   //console.log(result);
   if(response.ok){
     const jsonResponse = await response.json();
     alert('Response: ' + JSON.stringify(jsonResponse,'',2));
     console.log(jsonResponse.data); // array data: [{}...] 
     // 1.aici trebuie sa parcurgem array de obiecte  si sa afisam "title", "link", "album.title" 
     // si sa ii stabilim la setResult
     // 2. sa afisam ce contine setResult in Results div
    setResult(jsonResponse.data);        
   }

  } catch (error) {
    console.error(error);
  }


} 



  useEffect(()=>{

    // cerem lista de songs dupa artist
   const songs = getSongsbyArtist();


  }, []);

  function handleChangeInput(e){
     e.preventDefault();
     setSearchInput(e.target.value);
  }

  // onClickSearch prop
  function handleClickSearchButton(e){
    // previne sa revina la starea initiala , deci nu mai dispare rezultatul
    e.preventDefault();
    //setResult(searchInput);



  }

  return (
    <div style={app}>
      <h1 style={title}><span style={span}>LISTEN</span>your favorite<span style={span}>MUSIC</span></h1>
      <div style={container}>
        <SearchBar searchInput={searchInput} onChangeInput={handleChangeInput} onClickSearch={handleClickSearchButton}/>
        <div style={resultAndPlaylist}>
          <SearchResults result={result}/>
          <Playlist/>
        </div> 
      </div>
      <div >{JSON.stringify(result)}</div>
    </div>
  );
}


export default App;
